import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";




const Cart = () => {

  const {cart} = useSelector((state) => state);
  console.log("Printing Cart");
  console.log(cart);
  const [totalAmount, setTotalAmount] = useState(0);

  useEffect( () => {
    setTotalAmount( cart.reduce( (acc, curr) => acc + curr.price,0) );
  }, [cart])

  return (
    <div>
  {
    cart.length > 0  ? 
    (<div>

     <div class="flex flex-row">
      <div class="ml-40 w-3/4">
        {
          cart.map( (item,index) => {
            return <CartItem key={item.id} item={item} itemIndex={index} />
          } )
        }
      </div>

      <div class="mr-40 w-1/4 h-10 border-double border-4 border-indigo-600 "> 

        <div class="flex flex-col px-8 py-10 ">
          <div class="font-semibold text-2xl border-b pb-8" >Your Cart</div>
          <div class="font-semibold text-2xl border-b pb-8 flex justify-between mt-10 mb-5">Summary</div>
          <p>
            <span>Total Items: {cart.length}</span>
          </p>
        </div>
        <br></br>
        <br></br>
        <br></br>
        <div>
          <p class="font-bold">Total Amount: ${totalAmount}</p>
          <button class="bg-green-500 hover:green-blue-900 text-white font-bold py-2 px-4 rounded">
            CheckOut Now
          </button>
        </div>

      </div>

</div>
    </div>) : 
    (<div>
      <h1>Cart Empty</h1>
      <Link to={"/"}>
        <button>
          Shop Now
        </button>
      </Link>
    </div>)
  }
    </div>
  );
};

export default Cart;
